                mn = min(mn, g[0][i]);
            }
            return mn;
        }else{
            priority_queue<int, vector<int>, greater<int> > pq;
            for(int i = 0; i < c; i++) pq.push(g[0][i]);
            // double loop
            for(int i = 1; i < r ; i++){
                for(int j = 0; j < c; j++){
                    if(pq.top() == g[i-1][j]){
                        int x = pq.top();
                        pq.pop();
                        g[i][j] += pq.top();
                        pq.push(x);
                    }else{
                        g[i][j] += pq.top();
                    }
                }
                while(!pq.empty()) pq.pop();
                for(int j = 0; j < c; j++) pq.push(g[i][j]);
            }

            return pq.top();
        }
    }
};
