1] TOP Down recursive -

	class Solution {
		public int minFallingPathSum(int[][] arr) {

			int min = Integer.MAX_VALUE;

			int[][] memo = new int[arr.length][arr[0].length];

			for (int[] m : memo) {
			  Arrays.fill(m, -1);    
			}

			for (int i=0;i<arr[0].length;i++) {

				int lm = mfp(arr, 0, i, memo);

				if (lm < min)
					min = lm;
			}

			return min;
		}

		public int mfp(int[][] arr, int cr, int cc, int[][] memo) {

			if (cr == arr.length)
				return 0;

			if (memo[cr][cc] != -1) {
				return memo[cr][cc];
			}

			int cv = arr[cr][cc];

			int min = Integer.MAX_VALUE;

			for (int c=0;c<arr[0].length;c++) {
				if (c != cc) {
					int rv = mfp(arr, cr+1, c, memo);

					if (rv < min) {
						min = rv;
					}
				}
			}

			memo[cr][cc] = min+cv;

			return memo[cr][cc];
		}
}

