class Solution {
    public int minFallingPathSum(int[][] arr) {
        int n = arr.length;
        int m = arr[0].length;
        // states
        int[][] f = new int[n][m];
        Queue<int[]> prevQueue = new PriorityQueue<>(new Comparator<int[]>() {
            @Override
            public int compare(int[] i, int[] j) {
                return j[1] - i[1];
            }
        });
        // initialize
        for (int j = 0; j < m; j++) {
            f[0][j] = arr[0][j];
            prevQueue.offer(new int[]{j, f[0][j]});
            if (prevQueue.size() > 2) {
                prevQueue.poll();
            }
        }
        // function
        for (int i = 1; i < n; i++) {
            Queue<int[]> newQueue = new PriorityQueue<>(new Comparator<int[]>() {
                @Override
                public int compare(int[] i, int[] j) {
                    return j[1] - i[1];
                }
            });
            int[] prevMin2 = prevQueue.poll();
            int[] prevMin1 = prevQueue.poll();
            for (int j = 0; j < m; j++) {
                if (prevMin1[0] != j) {
                    f[i][j] = prevMin1[1] + arr[i][j];
                } else {
                    f[i][j] = prevMin2[1] + arr[i][j];
                }
                newQueue.offer(new int[]{j, f[i][j]});
                if (newQueue.size() > 2) {
                    newQueue.poll();
                }
            }
            prevQueue = newQueue;
        }
        // answer
        int ans = Integer.MAX_VALUE;
        for (int i : f[n - 1]) {
            ans = Math.min(ans, i);
        }
        return ans;
    }
}
