class Solution {
public:
    int minFallingPathSum(vector<vector<int>>& arr) {       //function with square grid input

        int n = arr.size() ;                        //int n is size of the array, number of rows
        vector<int> dp ;                            //dp vector of ints
        dp = arr[n-1] ;                             //dp is assigned as the array last row

        for(int i=n-2;i>=0;i--){                    //outer for loop starting from second to last row, decrementing

            int firstmin=INT_MAX,secondmin=INT_MAX,firstminindex ;    //min defines

            for (int j=0;j<n;j++){                  //for loop
                if (firstmin>dp[j]){                    //calculating first min 
                    firstmin = dp[j] ;                  //find the smallest value in the row
                    firstminindex = j ;                 //find the index of the smallest value
                }

            }

            for (int j=0;j<n;j++){                  //for loop
                if (j==firstminindex)continue ;     //if col index the same as prev. min, just continue to next col.
                secondmin = min(secondmin,dp[j]) ;  //find the next highest number
            }

            for (int j=0;j<n;j++){                  //for loop
                if (j==firstminindex) dp[j]=arr[i][j]+secondmin ;  //if index is a hit, then set the number equal to the array plus second lowest number
                else dp[j] = arr[i][j]+firstmin ;                  //else set the number equal to the array plus the firstmin
            }
        }

        sort (dp.begin(),dp.end());             //sort low to high

        return dp.front(); //return the smallest val from dp
    }
};

