class Solution {
    public int minFallingPathSum(int[][] A) {

        // Initializing minimum number and second minimum number in first row, and resp. column indices.
        // Example: [1, 3, 2] => (min1 = 1, col1 = 0), (min1 = 2, col2 = 2)
        int min1 = Integer.MAX_VALUE, col1 = -1, min2 = Integer.MAX_VALUE, col2 = -1;
        for(int j=0; j<A[0].length; j++) {
            if (A[0][j] < min1) {
                min2 = min1;
                col2 = col1;
                min1 = A[0][j];
                col1 = j;
            } else if (A[0][j] < min2) {
                min2 = A[0][j];
                col2 = j;
            }
        }

        for(int i=1; i<A.length; i++) {
            int newMin1 = Integer.MAX_VALUE, newCol1 = -1, newMin2 = Integer.MAX_VALUE, newCol2 = -1;

            for(int j=0; j<A[0].length; j++) {
                // If same column as minimum number, choose second minimum number
                if (j == col1) A[i][j] += min2;
                // For the rest of the row members, choose first mimimum number
                else A[i][j] += min1;

                // Store new minimum numbers and respective column indices
                if (A[i][j] < newMin1) {
                    newMin2 = newMin1;
                    newCol2 = newCol1;
                    newMin1 = A[i][j];
                    newCol1 = j;
                } else if (A[i][j] < newMin2) {
                    newMin2 = A[i][j];
                    newCol2 = j;
                }
            }
            // Update original minimum numbers and respective column indices
            min1 = newMin1; min2 = newMin2;
            col1 = newCol1; col2 = newCol2;
        }
        return min1;
    }
}
