    public boolean isValid(int m,int n,int i,int j){
        return i>=0 && i<m && j>=0 && j<n;
    }
    public int solve(int [][]arr,int i,int j){
        int m = arr.length,n = arr[0].length;
        if(i==m-1){
            dp[i][j] = arr[i][j];
            return dp[i][j];
        }
        if(dp[i][j]!=Integer.MAX_VALUE){
            return dp[i][j];
        }
        int min = Integer.MAX_VALUE;
        for(int k=0;k<n;k++){
            if(k!=j){
                dp[i+1][k] = solve(arr,i+1,k);
                min = Math.min(dp[i+1][k],min);
            }
        }
        dp[i][j] = arr[i][j] + min;
        return dp[i][j];
    }
    public int minFallingPathSum(int[][] arr) {
        int m = arr.length, n = arr[0].length;
        dp = new int[m][n];
        for(int []d : dp){
            Arrays.fill(d,Integer.MAX_VALUE);
        }
        int solution = Integer.MAX_VALUE;   
        for(int j=0;j<n;j++){
            int val = solve(arr,0,j);
            solution = Math.min(solution,val);
        }
        return solution;
    }
}
