        int n=arr.size(), m=arr[0].size();
        vector<int> pre(m), suff(m);
        for(int j=0; j<m; j++)
            if(j>0)
                pre[j]=min(pre[j-1], arr[0][j]);
            else
                pre[j]=arr[0][j];
        for(int j=m-1; j>=0; j--)
            if(j+1<m)
                suff[j]=min(suff[j+1], arr[0][j]);
