        for(int i=1; i<n; i++){
            for(int j=0; j<m; j++){
                int left=1e4, right=1e4;
                if(j-1>=0)
                    left=pre[j-1];
                if(j+1<m)
                    right=suff[j+1];
                arr[i][j]+=min(left, right);
            }
            pre=suff=vector<int>(m);
            for(int j=0; j<m; j++)
                if(j>0)
                    pre[j]=min(pre[j-1], arr[i][j]);
                else
                    pre[j]=arr[i][j];
            for(int j=m-1; j>=0; j--)
                if(j+1<m)
                    suff[j]=min(suff[j+1], arr[i][j]);
                else
                    suff[j]=arr[i][j];
        }
        return *min_element(arr[n-1].begin(), arr[n-1].end());
    }
