                min = row[i];
                idx = i;
            } else if (row[i]<min_2) {
                min_2 = row[i];
                idx_2 = i;
            }
        }
        custom[0] = min;
        custom[1] = idx;
        custom[2] = min_2;
        custom[3] = idx_2;
        return custom;
    }

