class Solution {
public:
    int minFallingPathSum(vector<vector<int>>& arr) {
         multiset<int>ms;
         int res=INT_MAX;
         int n=arr.size();
         vector<vector<int>>dp(n,vector<int>(n));
         for(int j=0;j<n;j++)                    //First row cost 
         {
             dp[0][j]=arr[0][j];
             ms.insert(dp[0][j]);
         }
         for(int i=1;i<n;i++)
         {
             for(int j=0;j<n;j++)
             {
                 ms.erase(ms.find(dp[i-1][j]));                     //erase the just above element
                 dp[i][j]=*ms.begin()+arr[i][j];                    // find the minimum for current cell
                 ms.insert(dp[i-1][j]);                    // insert to the multiset the val we have deleted 
