               ms.clear();                              //insert cur row elements to the multiset now
               for(int j=0;j<n;j++)
               ms.insert(dp[i][j]);
             }
         }
         for(int j=0;j<n;j++)                                     // Find the final res
            res=min(res,dp[n-1][j]);
         return res;    
    }
};
