class Solution:
    def minFallingPathSum(self, arr: List[List[int]]) -> int:
        dp = arr[0][:]
        R, C = len(arr), len(arr[0])
        if R==1:return min(dp)
        for i in range(1, R):
            new_dp = arr[i][:]
            for j in range(C):
                new_dp[j] += min(dp[jj] for jj in range(C) if jj!=j) 
            dp = new_dp[:]
        return min(dp)
