class Solution(object):
    def minFallingPathSum(self, arr):
        for ii in range(0, len(arr) - 1):
            heap = []

            for jj in range(len(arr[ii])):
                heapq.heappush(heap, (-arr[ii][jj], jj))
                if len(heap) > 2:
                    heapq.heappop(heap)

            v,u = heapq.heappop(heap), heapq.heappop(heap)
            u = [-u[0], u[1]] # min and index of min
            v = [-v[0], v[1]] # second min and index of second min

            for jj in range(len(arr[ii + 1])):
                if jj != u[1]:
                    arr[ii+1][jj] += u[0]
                else:
                    arr[ii+1][jj] += v[0]
        return min(arr[-1])
