class Solution {
    PriorityQueue<int[]> queue = new PriorityQueue<>((a,b)->b[1] - a[1]);
    public int minFallingPathSum(int[][] grid) {
        int m = grid.length;
        int n = grid[0].length;
        // heap: index and its value

        for (int j = 0; j<n; j++) {
            getTwoMin(0, grid);
        }

        for (int i = 1; i<m; i++) {
            int[] minIndex = getIndexFromHeap();

            for (int j = 0; j<n; j++) {
                // step 1: update the cur i, j from previous heap
                if (j != minIndex[1]) {
                    grid[i][j] += grid[i-1][minIndex[1]];
                } else{
                    grid[i][j] += grid[i-1][minIndex[0]];
                }
            }
            getTwoMin(i, grid);
        }
        int[] minIndex = getIndexFromHeap();
        System.out.println(minIndex[0]);
        int ans = Integer.MAX_VALUE;
        for (int j = 0; j<n; j++) {
            ans = Math.min(ans, grid[m-1][j]);
        }
        return n>=2 ? grid[m-1][minIndex[1]] : grid[m-1][minIndex[0]] ;
    }   
    public void getTwoMin(int rowIndex, int[][] grid) {
        queue.clear();
        for (int j = 0; j<grid[0].length; j++) {
            queue.add(new int[]{j, grid[rowIndex][j]});
            if (queue.size() > 2) {
                queue.poll();
            }
        }
    }
    public int[] getIndexFromHeap(){
        int index = 0;
        int[] minIndex = new int[2];
        minIndex[0] = -1;
        minIndex[1] = -1;
        while (!queue.isEmpty()) {
            int[] element = queue.poll();
            minIndex[index++] = element[0];
        }
        return minIndex;
    }
}
