class Solution {
    public int minFallingPathSum(int[][] matrix) {
        int n = matrix.length;
        int m = matrix[0].length;

        int dp[][] = new int[n][m];

        for(int  i = 0; i < n; i++){
            for(int j = 0; j < m; j++){
                dp[i][j] = -1;
            }
        }

        int ans = (int)1e9;
        for(int col = 0; col < m; col++){
            ans = Math.min(ans, f(n-1, col, matrix, dp));
        }
        return ans;
    }

    //recusrion + Memo
    public int f(int i, int j, int[][]a, int dp[][]){
        int m = a[0].length;
        if(i < 0 || j >= m || j < 0) return (int)1e9; // invalid case
        if(i == 0) return a[0][j]; //we've reached to the end

        if(dp[i][j] != -1) return dp[i][j];

        // here is the slight modificatio than Minimum falling path sum 1
        // just try out all possible answers from the previous row where current col is not equal to the previous col.
        int res = (int) 1e9;
        for(int col = 0; col < m; col++){
            if(col != j){
                int call = a[i][j] + f(i-1, col, a, dp); 
                res = Math.min(call, res);
            }
        }
        return dp[i][j] = res;
    }
}


