class Solution {
    public int minFallingPathSum(int[][] grid) {
        this.memo = new int[grid.length][grid[0].length];
        for (int[] row : memo) Arrays.fill(row, -1);
        int min = Integer.MAX_VALUE;
        for (int i = 0;i < grid[0].length;i++) {
            min = Math.min(min, dp(grid,0, i));
        }
        return min;
    }

    private int[][] memo;

    public int dp(int[][] grid, int row, int col) {
        if (memo[row][col] != -1) {
            return memo[row][col];
        }

        int minPath = Integer.MAX_VALUE;

        for (int i = 0;i < grid.length && row != grid[0].length-1;i++) {
            if (i == col) {
                continue;
            }

            minPath = Math.min(minPath, dp(grid, row+1, i) + grid[row][col]);
        } 
        if (minPath == Integer.MAX_VALUE) minPath = grid[row][col];
        memo[row][col] = minPath;
        return minPath;
    }
}
