class Solution {
public:
    int minFallingPathSum(vector<vector<int>>& grid) {
        int n=grid.size();
        vector<vector<int>> dp(n,(vector<int>(n,0)));
        for(int i=n-1;i>=0;i--)
        {
            dp[n-1][i]=grid[n-1][i];
        }
        for(int i=n-2;i>=0;i--)
        {
            for(int j=n-1;j>=0;j--)
            {
                int mini=INT_MAX;
                for(int k=0;k<n;k++)
                {
                    if(k!=j)
                    {
                        mini=min(mini,dp[i+1][k]);
                    }
                }
                dp[i][j]=grid[i][j]+mini;
            }
        }
        return *min_element(dp[0].begin(),dp[0].end());
    }
};
```

