var getDecimalValue = function(head, total = 0) {
									//^Set a default parameter named total with the value of 0
  //While there is a head node...
  while (head) {
      //double the value of total and sum it up with the value of the head node.
      total = total * 2 + head.val;
      //Set the head node pointer to look at the next node.
      head = head.next;
  }
  //return the total after looking through all the digits.
  return total;
};

getDecimalValue([1, 0, 1]); //Expected Output: 5
