```
public class Solution
{
    public int GetDecimalValue(ListNode head)
    {
        StringBuilder sb = new(30);

        while (head is not null)
        {
            sb.Append(head.val);
            head = head.next;
        }

        return Convert.ToInt32(sb.ToString(), 2);
    }
