class Solution {
public:
    int getDecimalValue(ListNode* head) {
        vector<int> v;
        while(head!=NULL){
            v.push_back(head->val);
            head = head->next;
        }
        reverse(v.begin(),v.end());
        int n = v.size();
        int ans=0;
        for(int i=0;i<n;i++){
            ans+=pow(2,i)*v[i];
        }
        return ans;
    }
};

