class Solution {
public:

    int l , h , num; 
    vector <int> v;

    void backtrack(int n)
    {
        if(n > 9)
            return; 
        num = num*10 + n;
        if(num >= l && num <= h)
        {
            v.push_back(num); 
        }
        if(num > h)
            return; 
        backtrack(n+1);  
    }

    vector<int> sequentialDigits(int lo, int hi) 
    {
        l = lo , h = hi; 
        for(int i=1;i<9;i++)
        {
            num = i; 
            backtrack(i+1); 
        }
        sort(v.begin() , v.end()); 
        return v; 
    }
};

