    public void helper(String temp, int low, int high, List<Integer> list) {
        int num = Integer.parseInt(temp);
        int next = Integer.parseInt(temp.substring(temp.length() - 1)) + 1;
        if (num > high) {
            return;
        }
        if (num >= low && num <= high) {
            list.add(num);
        } 
        if (next < 10) {
            helper(temp + next, low, high, list);
        }
    }
}
