class Solution {
public:

	int prev_small(int digit) {     // to calculate smallest value for a given digit.
        int number=1;
        int previous_smaller=0;
        for(;number<=digit;number++) previous_smaller= previous_smaller*10+number;
        return previous_smaller;
    }


    vector<int> sequentialDigits(int low, int high) {
        vector<int> ans;
        int digit = log10(low)+1;
        int res=prev_small(digit);

        while(res<low) {              // to make our res greater or equal to low
            int last_digit = res%10;
            if(last_digit==9) {      // if last digit is 9 then we should increase digit count
                digit++;
                res = prev_small(digit);
                continue;
            }
            int power = pow(10,digit-1);
            res = (res - (res/power)*power)*10 + last_digit+1;
        }


        while(res<=high) {

            ans.push_back(res);
            int last_digit = res%10;
            if(last_digit==9) {
                digit++;
                res = prev_small(digit);
                continue;
            }
            int power = pow(10,digit-1);
            res = (res - (res/power)*power)*10 + last_digit+1;
        }

        return ans;
    }
};

