class Solution {
	//creating them class members bcz they will never get changed for an input
    int low=0;
    int high=0;
    public List<Integer> sequentialDigits(int low, int high) {
		//initializing all variables
        List<Integer> list=new ArrayList<>();
        this.high=high;
        this.low=low;

		//all of posible values will start from 1 to 9
        for(int i=1;i<=9;i++){
			//calling our helper fxn to get all possible values starting with our given digit
            dfs(i,list);
        }
		//sorting all found values as required in ques
        Collections.sort(list);
        return list;
    }

	//helper fxn to get all possible values associated with one started digit
    public void dfs(int num,List<Integer> list){
		//getting last digit from our no. and adding 1(to maintain sequential order)
        int add=(num%10)+1;
		//special case to be noted: 
		//when we have 9 as last digit
		//then our "add" will have 10 and sequential order will break so ignoring that case
        if(add!=10){
			//adding our sequence to our no.
            num=(num*10)+add;
			//if less than high then always try to check further
            if(num<=high){
				//if less than low i.e. num is in the range so add to list
                if(num>=low){
                    list.add(num);
                }
				//calling furhter to get more values less than high 
                dfs(num,list);
            }
        }

    }
}
