class Solution {
public:
    vector<int> sequentialDigits(int low, int high) {
        vector<int> dp;

        for(int i=1;i<9;i++){
            dp.push_back(i);
        }

        int counter = 2;
        int count = counter;

        vector<int> ans;
        while(count<=9){
            int i = 0;
            while(counter<=9){
              int num = dp[i] * 10 + counter;
              dp[i] = num;

              if(dp[i]>=low && dp[i]<=high)
                 ans.push_back(dp[i]);

              counter++;
              i++;  
            }
            count++;
            counter = count;
       }


       return ans; 


    }
};
