vector<int> sequentialDigits(int low, int high) {
	queue<int> q;
	for (int i = 1; i <= 9; i++)
		q.push(i);
	vector<int> res;
	while (!q.empty()) {
		int cur = q.front(); q.pop();
		if (cur >= low && cur <= high)
			res.push_back(cur);
		int next = cur * 10 + cur % 10 + 1;
		if (next % 10 && next <= high)
			q.push(next);
