function sequentialDigits($low, $high) {
        $nums = [];
        $s = '123456789';

        for($digits = strlen($low); $digits <= strlen($high); $digits++)
            for($start = 0; ($start+$digits) <= 9; $start++) {
                $num = (int)substr($s, $start, $digits);
