from itertools import combinations

class Solution:
    def sequentialDigits(self, low: int, high: int) -> List[int]:
        s = '123456789'
        all_possible = [int(s[i:j]) for i, j in combinations(range(len(s) + 1), 2)]
        return sorted(x for x in all_possible if low <= x <= high)

