class Solution {
public:
    vector<int> sequentialDigits(int low, int high) {
        vector<int> ans;
        for(int start=1;start<=9;start++){
            int num = start, prev = start;
            while(num<=high){
                if(num>=low)  
					ans.push_back(num);
                if(prev == 9) 
					break;
                prev++;
				num = num*10+prev;
            }
        }
        sort(ans.begin(),ans.end());
        return ans;
    }
};
