class Solution {
    public List<Integer> sequentialDigits(int low, int high) {
        List<Integer> ans=new ArrayList<>();
		//get number of digits in low
        int ds=getDigit(low);
		//get the last valid number of the corresponding digit
        int ls=getLast(ds);
		//if the last valid number if less than low for example low=90 here ls=89 
        if(low>ls){
		/* hence increase the number of digit as for low=90 the 
		first element that will come in the answer will be 123*/
            ds++;
			//now get the last valid number of the number of digit
            ls=getLast(ds);
        }
		//get the first element of the number of digit
        int fs=getFirst(ds);
		/*calculating the number which ill be added inorder to get the next valid number
		for 2 digit :  11
		3 digit : 111 */
        int dd=ds;
        int add=0;
        while(dd!=0){
            add=add*10+1;
            dd--;
        }
		//taking fs to the value which will surely be added
        while(fs<low)   fs=fs+add;


        while(fs<=high){
            /*for every number of digits we will run loop 
			for digit 2 loop will run from 12-89
			for digit 3 123-789*/
            while(fs<=ls){
                if(fs>high)
                    return ans;
                ans.add(fs);
                fs=fs+add;
            }
            if(fs>high)
                return ans;
			//since d digit answer has been added now increment the digut and get new first and last 
            ds++;
			//get the new add 11->111 111->1111
            add=add*10+1;
            fs=getFirst(ds);
            ls=getLast(ds);
        }

        return ans;
    }
	//returns number of digits in the number
    public int getDigit(int n){
        int ans=0;
        while(n!=0){
            ans++;
            n=n/10;
        }
        return ans;
    }
    /*returns the first sequential number of the required number of digit
	2 digit-> 12
	3 digit-> 123
	4 digit-> 1234
	*/
    public int getFirst(int d){
        int dd=1;
        int ans=0;
        while(d!=0){
            ans=ans*10+dd;
            dd++;
            d--;
        }
        return ans;
    }
    /*returns the last sequential number of the required number of digit
	2 digit-> 89
	3 digit-> 789
	4 digit-> 6789
	*/
    public int getLast(int d){
        int dd=10-d;
        int ans=0;
        while(d!=0){
            ans=ans*10+dd;
            dd++;
            d--;
        }
        return ans;
    }
}
