class Solution {
    public List<Integer> sequentialDigits(int low, int high) {

        List<Integer> mr = new ArrayList<>();

		for (int i = 1; i <= 9; i++) {
			digits(low, high, 0, i, mr);
		}

        Collections.sort(mr);//sorting

		return mr;
	}
	private void digits(int low, int high, int ans, int n, List<Integer> mr) {

		if(ans > high) {
			return;
		}
		if(ans >= low) {
			mr.add(ans);
		}
		if(n > 9) {
			return;
		}
		digits(low, high, ans * 10 + n, n + 1, mr);
    }
}
