class Solution {
public:
    long long int n=0;
    vector<int>res;
    vector<int> sequentialDigits(int low, int high) {

        long long int low1=low; 

        while(low1<high)
        {
            for(int i=1;i<=9;i++)
                produce(low1,high,i);
            low1*=10;
        }

        sort(res.begin(),res.end());
        return res;

    }

    void produce(int low,int high,int d)
    {
        if(n>high)
        {
            n=0;
            return;
        }

        if(n>=low&&n<=high)
        {
            res.push_back(n);
            n=0;
            return;
        }

        if(d<=9)
        {
            n=n*10+d;
            produce(low,high,d+1);
            n=0;
         }

        return;
    }

};
