class Solution:
    def sequentialDigits(self, low: int, high: int) -> List[int]:

        s = "123456789"
        res = []

        for k in range(1, len(s) + 1):
            for i in range(len(s) - k + 1):
                x = int(s[i:i+k]) 
                if x >= low and x <= high:
                    res.append(x)

        return res
