class Solution {
    List<Integer> ls;
    public List<Integer> sequentialDigits(int low, int high) {
        ls=new ArrayList<>();

        String lows=low+"";
        String highs=high+"";

        for(int i=1;i<=9;i++){
            makeNum(low,lows.length(),high,highs.length(),"",i);
        }

        Collections.sort(ls);
        return ls;
    }

    public void makeNum(int start, int startLen, int high, int highLen, String str, int nextD){
        int num=str.equals("")?-1:Integer.parseInt(str);

        if(num>high){
            return;
        }

        if(str.length()>=startLen && str.length()<=highLen){
            if(num>=start && num<=high){
                ls.add(num);
            }
        }

        if(nextD>=10){
            return;
        }


        makeNum(start,startLen,high,highLen,str+nextD,nextD+1);

    }
}

