class Solution{
    public List<Integer> sequentialDigits(int low, int high) {
        List<Integer> ans=new ArrayList<>();
        String digits="123456789";

        String lows=low+"";
        int end=lows.length();

        int start=0;
        int prev=end;
        boolean flag=true;

        while(flag && start<=8){
            if(start+end>9){
                flag=false;
            }
            for(int i=start;i+end<=9 && flag;i++){
                String sub=digits.substring(i,i+end);
                int num=Integer.parseInt(sub);
                if(num<low){
                    continue;
                }else if(num>high){
                    flag=false;
                    break;
                }else{
                    ans.add(num);
                }
            }

            if(flag){
                start=0;
                end++;
            }
        }

        return ans;
    }
}
