class Solution:
    def sequentialDigits(self, low: int, high: int) -> List[int]:
        s = "123456789"
        # moivng window lengh == min_len
        min_len = len(str(low))
        max_len = len(str(high))
        res = []
        while min_len <= max_len:
            for i in range(0, len(s) - min_len + 1):
                n = int(s[i : i + min_len])
                if low <= n <= high:
                    res.append(n)
            min_len += 1
        return res

