```c++
class Solution {
public:
	//function that returns the number of digits in an integer
    int countDigits(int low) {
        int digits = 0;
        while(low) {
            low /= 10;
            digits++;
        }
        return digits;
    }

    vector<int> sequentialDigits(int low, int high) {
		//this is the maximum number of which all the other sequences are substrings of
		//we'll be taking substrings from this number, converting the substring to int
		//and adding the number to the vector
        string n = "123456789";
		//vector that's to be returned
        vector<int> answerSet;
		//get the current substring length
		//substring length should be between low and high inclusive
		//we'll be initialising this with the number of digits in low (our starting point)
        int currSubString = countDigits(low);

		//taking long to avoid integer overflow
        for(long int i = 10; i <= high; i*=10){
			//inner loop to traverse the string (containing the max. number) and extract the substrings from it
            for(int j = 0; j < 10; j++) {
				//checking if the combined length of offset and substring length is less than 10
                if(currSubString + j < 10) {
					//checking if the substring integer value is within low and high. if true, add it to vector
                    if(stoi(n.substr(j, currSubString)) <= high && stoi(n.substr(j, currSubString)) >= low) {
                        answerSet.push_back(stoi(n.substr(j, currSubString)));
                    }
                }
            }
			//after each pass, increasing the substring length by 1.
			//example, 123,234,...,1234
            currSubString++;
        }

        return answerSet;
    }
};
