class Solution {
    public List<Integer> sequentialDigits(int low, int high) {
        List<Integer> res = new ArrayList<>();
        int len = String.valueOf(low).length(),i=1;

        StringBuilder sb = new StringBuilder();
        for(i=1;i<=len;i++) sb.append(i);

        while(true){
            int num = Integer.parseInt(sb.toString());
            if(num >= low && num <= high) res.add(num);
            if(num > high) break;

			// if last digit is 9 then we increment size of num  Eg. 6789 to 12345
            if(i > 9){     
               sb.setLength(0);   // clear sb
                ++len;               
                if(len > 9) break;   // since max sequential no. possible is 123456789
               for(i=1;i<=len;i++) sb.append(i);  
            }
            else{   
            sb.delete(0,1);
            sb.append(i++);   
            }
        }
        return res;
    }
}
