    def sequentialDigits(self, low: int, high: int) -> List[int]:
        string = "123456789"

        ans = []

        for i in range(1, 10):
            for j in range(8):
                temp = int(string[j: j+i], 10)

                if(j+i>9 or temp>high): 
                    break

                if(low<=temp<=high):
                    ans.append(temp)

        return ans
