class Solution {
public:
    vector<int> sequentialDigits(int low, int high) {
        vector<int> nums, sol;
        for (int len = 2; len <= 9; ++len) { // Length of sequence numbers ranges from 2 to 9.
            for (int st = 1; st <= 8; ++st) { // No sequence number can start with nine they start with any number in the range [1, 8].
                if (st + len - 1 > 9) break; // For example no sequence number that starts with 8 can have a length of more than 2.
                int num = 0;
                for (int i = st; i < st + len; ++i) {
                    num *= 10; num += i; // Generate all sequence numbers.
                }
                nums.push_back(num); // Generated array is sorted.
            }
        }
        int st = lower_bound(nums.begin(), nums.end(), low) - nums.begin(); // Binary Search on first element in the solution.
        for (int i = st; i < nums.size(); ++i) {
            if (nums[i] > high) break; // Exit the loop if the high range is violated.
            sol.push_back(nums[i]);
        }
        return sol;
    }
};
