class Solution {
public:
    int longestValidParentheses(string s) {
        vector<int>v(s.size()+1,0);
        for(int i=1;i<s.length();i++){
            if(s[i]==')'){// when s[i]==')' and one index before it is'(' than v[i] ie no of valid parenthesis upto index i is v[i-2]+2
                if(s[i-1]=='('){
