class Solution:
    def longestValidParentheses(self, s):
        """
        :type s: str
        :rtype: int
        """
        l = len(s)

        # 扫描一遍，构造一个栈，最后只剩下匹配不上的索引
        stack = []
        for i in range(l):
            if s[i] == ')' and stack and s[stack[-1]] == '(':
                stack.pop()
            else:
                stack.append(i)
        # 不在栈内的索引是可以匹配上的，相邻索引之间的子串为有效子串
        if not stack:
            return l
        stack.insert(0, -1)
        stack.append(l)
        return max(stack[i+1]-stack[i]-1 for i in range(len(stack)-1))
