def longestValidParentheses(s: str) -> int:
        # Convert to an array of 1s
        stack = []
        ans = [0] * len(s)

        for i, char in enumerate(s):
            if not stack or char == "(":
                stack.append((char, i))

            elif char == ")":
                if stack[-1][0] == "(":
                    _, idx = stack.pop()
                    ans[i], ans[idx] = 1, 1
                else:
                    stack.append((")", i))

        # Now find the longest substring of 1s
        ones = 0
        ret = 0
        for char in ans:
            if char == 1:
                ones+=1
                ret = max(ret, ones)
            else:
                ones = 0

        return ret
