	class Solution {
	public:
		vector<vector<string>>ans;
		bool isPali(string s,int l,int r){
			while(l<r){
				if(s[l]!=s[r]){
					return false;
				}
				l++;
				r--;
			}
			return true;
		}
		void check(int ind,string &s,vector<string> &cur){
			if(ind==s.size()){
				ans.push_back(cur);
				return ;
			}
			for(int i=ind;i<s.size();i++){
				if(isPali(s,ind,i)){
					cur.push_back(s.substr(ind,i-ind+1));
					check(i+1,s,cur);
					cur.pop_back();
				}
			}
		}
		vector<vector<string>> partition(string s) {
			vector<string>cur;
			check(0,s,cur);
			return ans;
		}
	};

