class Solution {
public:
    int t[2001][2001];
    int pal[2001][2001]; // storing palindrome value also so that need not to calculate if already checked

    int minCut(string s) {
        int n = s.size();
        memset(t, -1, sizeof(t));
        memset(pal, 0, sizeof(t));
        int ans = partition(s, 0, n-1);
        return ans;
    }

public:
    int partition(string& s, int i, int j){
        if(i>=j || pal[i][j] == 1){
            return 0;
        }
        if(ispalindrome(s, i, j)==1){
            t[i][j] = 0;
            return 0;
        }

        if(t[i][j]!=-1){
            return t[i][j];
        }
        int temp_ans =0;
        int minval = s.size()-1; // if partitioning each character because no palindrome is present
        for(int k=i; k<j; k++){
            int right =0;
//             if left part is palindrome then check for right half else move one more position
//             eg. itika ---> so 'it' is not panlindrome move to next postition now, left half is 'iti' which 
            // which is a palindrome so check for its right half i.e. 'ka'
            if(ispalindrome(s, i, k) == 1){
                temp_ans = 1+ partition(s, k+1, j);
                minval = min(minval, temp_ans);
            }
        }
        t[i][j] = minval;
        return minval;
    }

public:
//     remember to pass it by reference rather than pass by value, because size of address < size of string 
//     in case of larger strings other it will give TLE
    int ispalindrome(string& s, int i, int j){
        if(i>=j){
            return true;
        }
        while(i<=j){
            if(s[i]!=s[j]){
                pal[i][j] =0;
                return 0;
            }
            else{
                i++;
                j--;
            }
        }
        pal[i][j] = 1;
        return 1;
    }
};
