class Solution {
    public int minCut(String s) {
        int n=s.length();
        boolean dp[][]=new boolean[n][n];

        //gap strategy (dp)
        //checking if this string is Palindrome or not
        for(int g=0;g<n;g++){
            for(int i=0,j=g;j<n;i++,j++){
                //only one element
                if(g==0){
                    dp[i][j]=true;
                }
                else if(g==1){
                    //two ele if equal
                    if(s.charAt(i)==s.charAt(j)){
                        dp[i][j]=true;
                    }
                    else{
                        dp[i][j]=false;
                    }
                }
                else{
                    //more than 2 ele's
                    //if first and last is equal
                    //and inside string is also equal
                    if(s.charAt(i)==s.charAt(j) && dp[i+1][j-1]){
                        dp[i][j]=true;
                    }
                    else{
                        dp[i][j]=false;
                    }
                }
            }
        }

        int dp2[]=new int[n];

        //for only one element (means Palindrome) no need to cut
        dp2[0]=0;

        //for filling ans dp (dp2)
        for(int j=1;j<n;j++){

            //if our string is already Palindrome then no need to cut so 0
            //imagine grid of true's and false's (imagine if we take all ele's)
            //and it's Palindrome so no need to cut/divide
            if(dp[0][j]){
                dp2[j]=0;
            }

            //string is not Palindrome (we need to make a cut)
            else{

                //taking min answer and store in dp2 (our answer array)
                int min=Integer.MAX_VALUE;

                //try to imagine a grid which is filled with true's and false's (dp)
                //take suffix and calculate answer
                //not going upto 0 bcz it means the whole string which we have already checked
                //another way of saying is - in grid going up (subtracting suffix)
                for(int i=j;i>=1;i--){

                    //if this suffix is Palindrome
                    if(dp[i][j]){

                        //then we can divide into
                        //we have suffix upto i
                        //we have our ans dp2 upto j
                        //means remaing string answer we have already calcualted, just take it from dp2
                        min=Math.min(min,dp2[i-1]);
                    }
                }

                //we make a cut for suffix means we have to add 1 for cut
                dp2[j]=min+1;
            }
        }

        //we have build our array for all values
        //this means we have taken into account all the elements
        //and our answer present in n-1 index
        return dp2[n-1];
    }
}
