class Solution:
    # [start, end)
    def is_palindrome(self, start, end):
        return self.a[start:end] == self.a[start:end][::-1]

    @lru_cache(None)
    def recurse(self, start):
        if start >= self.n: return 0

        count = +inf
        for end in range(start+1, self.n+1):
            if self.is_palindrome(start, end):
                count = min(count, 1+self.recurse(end))
        return count

    def minCut(self, a: str) -> int:
        self.a = a
        self.n = len(a)
        return self.recurse(0)-1
