class Solution {
public:
    bool isPalindrome(string s, int i, int j){
        while(i <= j){
            if(s[i] != s[j]) return false;
            i++;
            j--;
        }
        return true;
    }

    int solve(string s, int i, int j){
        //Base Case
        if(i>=j) return 0;
        if(isPalindrome(s,i,j)) return 0;

        int res = INT_MAX;

        //Move K
        for(int k=i; k<=j-1; k++){
            int temp = solve(s, i, k) + solve(s, k+1, j) + 1;
            res = min(res, temp);
        }

        return res;
    }
    int minCut(string s) {
        int i=0, j=s.size()-1;
        return solve(s,i,j);
    }
};
```
