class Solution {
    // Time: n ^ 2, Space: n
    unordered_map<int, int> map; // memoised based on index of the string
    int cut(string &s, int i) {
        if(i >= s.size()) return 0;
        if(map.count(i)) return map[i];

        int ans = INT_MAX;
        for(int j = i; j < s.size(); j++) { // looping for every possible case
            if(isPalin(s, i, j)) {
                ans = min(1 + cut(s, j + 1), ans);
            }
        }
        return map[i] = ans;
    }
    bool isPalin(string &s, int i, int j) {
        while(i < j) {
            if(s[i++] != s[j--]) return false;
        }
        return true;
    }
public:
    int minCut(string s) {
        return cut(s, 0) - 1; // subtracting 1 because cuts are 1 less then total palindromic substring
    }
};
```

