class Solution {

    func minCut(_ s: String) -> Int {

        var s = Array(s)

        var cuts = [Int](repeating: 0, count: s.count + 1)
        for i in 0...s.count { cuts[i] = i - 1 }

        var i = 0
        var j = 0
        while i < s.count {

            //Odd length palindromes.
            j = 0
            while i - j >= 0 && i + j < s.count && s[i-j] == s[i+j] {
                cuts[i + j + 1] = min(cuts[i + j + 1], 1 + cuts[i - j])
                j += 1
            }

            //Even length palindromes.
            j = 1
            while i - j + 1 >= 0 && i + j < s.count && s[i-j+1] == s[i+j] {
                cuts[i + j + 1] = min(cuts[i + j + 1], 1 + cuts[i - j + 1])
                j += 1
            }   
            i += 1
        }

        return cuts[s.count]
    }
}
